mapper.select:
SELECT alarm.collect_datetime, alarm.type, alarm.gps_address FROM device_alarm alarm WHERE alarm.device_sn = #{deviceSn}
and alarm.collect_datetime>=#{startTime}
and alarm.end_time <=concat(#{endTime},' 23:59:59')
and alarm.type=#{type}

SELECT * FROM device_alarm
WHERE device_sn = #{deviceSn} and alarm_no = #{alarmNo} and alarm_type = #{alarmType}
ORDER BY id desc limit 1

SELECT carDev.device_sn as deviceSn, car.lic_code as licCode, car.id as carId, etp.etp_name as etpName, suBrand.car_sub_brand as carSubBrand FROM car_info car LEFT JOIN car_device carDev ON car.id = carDev.car_id LEFT JOIN car_model model ON model.car_id = car.id LEFT JOIN car_sub_brand suBrand ON suBrand.id = model.sub_brand_id LEFT JOIN etp_info etp ON etp.id = car.etp_id WHERE car.del_flag = 0
and car.etp_id=#{query.etpId}
and car.lic_code like concat('%',#{query.licCode},'%')
and car.id = #{query.carId}
order by car.id , car.register_time desc

SELECT alarm.*, car.lic_code, gps.gps_addr FROM device_alarm alarm left join device_alarm_gps gps ON gps.id = alarm.sta_gps_id LEFT JOIN car_device carDev ON alarm.device_sn = carDev.device_sn LEFT JOIN car_info car ON car.id = carDev.car_id where alarm.is_del =0
and alarm.device_sn = #{query.deviceSn}
and alarm.sta_time >= #{query.startTime}
and alarm.end_time <= concat(#{query.endTime},' 23:59:59')
and alarm.alarm_type =#{query.alarmType}
and car.lic_code like concat('%',#{query.licCode},'%')
and car.etp_id=#{query.etpId}
order by alarm.create_time desc

SELECT
car.lic_code,
( CASE WHEN lastd.online = 0 or lastd.online is null   THEN 0    ELSE 1 END ) as online ,
lastd.lat,
lastd.gps_flag,
lastd.lng,
mapinfo.map_area_code1 AS mapAreaCode1,
mapinfo.map_area_code2 AS mapAreaCode2,
mapinfo.map_area_code3 AS mapAreaCode3
FROM
car_info car
INNER JOIN car_device carDev ON carDev.car_id = car.id
INNER JOIN etp_info etp ON etp.id = car.etp_id
LEFT JOIN device_last_data lastd ON lastd.device_sn = carDev.device_sn
LEFT JOIN driver_map_area_car_info mapinfo ON car.id = mapinfo.map_car_info_id
LEFT JOIN driver_map_sys_area map ON map.tree_names = mapinfo.map_area_name
WHERE
car.del_flag = 0

    and car.lic_code like concat('%',#{query.licCode},'%')


    and car.dept_id in
    #{item}


    and car.id in
    #{item}


    and car.etp_id in
    #{item}

/*上线，离线*/

    and  ( CASE WHEN lastd.online = 0 or lastd.online is null   THEN 0    ELSE 1 END )=#{query.online}

/*运行*/

    and lastd.car_status=1 or lastd.car_status=2

/*静止*/

    and lastd.car_status=0

/*地图查找*/
/*省级*/

    and mapinfo.map_area_code1 =#{query.mapAreaCode1}

/*市级*/

    and mapinfo.map_area_code2 =#{query.mapAreaCode2}

/*区级*/

    and mapinfo.map_area_code3 =#{query.mapAreaCode3}

order by car.create_time desc

SELECT
etp.etp_name,
car.lic_code,
carDev.device_sn,
lastd.gps_time,
lastd.gps_flag,
lastd.online,
lastd.speed,
car.id as carId,
lastd.direction,
lastd.lat,
lastd.lng,
lastd.update_time,
mapinfo.map_area_code1 as mapAreaCode1,
mapinfo.map_area_code2 as mapAreaCode2,
mapinfo.map_area_code3 as mapAreaCode3
FROM
car_info car
INNER JOIN car_device carDev ON carDev.car_id = car.id
INNER JOIN etp_info etp ON etp.id = car.etp_id
LEFT JOIN device_last_data lastd ON lastd.device_sn = carDev.device_sn
LEFT JOIN driver_map_area_car_info mapinfo ON car.id = mapinfo.map_car_info_id
LEFT JOIN driver_map_sys_area map ON map.tree_names = mapinfo.map_area_name
where car.del_flag=0

    and car.lic_code like concat('%',#{query.licCode},'%')


    and car.dept_id in
    #{item}


    and car.id in
    #{item}


    and car.etp_id in
    #{item}

/*上线，离线*/

    and  ( CASE WHEN lastd.online = 0 or lastd.online is null   THEN 0    ELSE 1 END )=#{query.online}

/*运行*/

    and lastd.car_status=1 or lastd.car_status=2

/*静止*/

    and lastd.car_status=0

/*地图查找*/
/*省级*/

    and mapinfo.map_area_code1 =#{query.mapAreaCode1}

/*市级*/

    and mapinfo.map_area_code2 =#{query.mapAreaCode2}

/*区级*/

    and mapinfo.map_area_code3 =#{query.mapAreaCode3}

order by car.create_time desc

SELECT etp.etp_name, car.lic_code, carDev.device_sn, lastd.gps_time, lastd.online, lastd.speed, lastd.direction, lastd.lat, lastd.lng, lastd.update_time FROM car_info car INNER JOIN car_device carDev ON carDev.car_id = car.id INNER JOIN etp_info etp ON etp.id = car.etp_id LEFT JOIN device_last_data lastd ON lastd.device_sn = carDev.device_sn where car.del_flag=0
and car.etp_id=#{query.etpId}
and car.id=#{query.carId}
and car.lic_code like concat('%',#{query.licCode},'%')
and car.dept_id=#{query.deptId}
/*上线，离线*/
and ( CASE WHEN lastd.online = 0 or lastd.online is null THEN 0 ELSE 1 END )=#{query.online}
/*运行*/
and lastd.car_status=1 or lastd.car_status=2
/*静止*/
and lastd.car_status=0
order by car.create_time desc

SELECT a.area_code AS "areaCode", a.area_name AS "areaName", a.area_type AS "areaType" FROM driver_map_sys_area a WHERE a.status = 0
AND a.parent_code = #{area.areaCode}
AND a.area_type = #{area.areaType} ORDER BY a.tree_sorts, a.area_code

SELECT IFNULL(COUNT(1),0) FROM car_fence_remind  re
LEFT JOIN car_info car ON re.car_id=car.id
WHERE car.etp_id=#{etpId} and re.create_time>=#{date}
