mapper.update:
UPDATE device_trip set over_speed = over_speed + #{overSpeed},
        sharp_accelerate = sharp_accelerate + #{sharpAccelerate},
        sharp_slowdown = sharp_slowdown + #{sharpSlowdown},
        sharp_swerve = sharp_swerve+ #{sharpSwerve}
 WHERE device_sn = #{deviceSn} AND sta_time = #{staTime}

mapper.select:
SELECT car.lic_code, trip.*, round(TIMESTAMPDIFF( second, trip.sta_time, trip.end_time )/3600,1)AS timeLong -- , -- (select sum(round(TIMESTAMPDIFF( second, sta_time,end_time )/3600,1) ) from device_trip) -- as totaltimeLong FROM device_trip trip left join car_device cd on trip.device_sn = cd.device_sn left join car_info car on car.id = cd.car_id left join etp_info etp on etp.id = car.etp_id
car.del_flag = 0 and car.etp_id in
#{item}
and trip.device_sn =#{query.deviceSn}
and car.lic_code like concat('%',#{query.licCode},'%')
and trip.sta_time >= #{query.startTime}
and trip.end_time <= concat(#{query.endTime},' 23:59:59')
order by trip.create_time desc

SELECT SUM(trip.mileage) as totalMileage , SUM(trip.fuel_consumption) as totalFuels, SUM(round(TIMESTAMPDIFF( second, trip.sta_time, trip.end_time )/3600,1))AS totaltimeLong FROM device_trip trip left join car_device cd on trip.device_sn = cd.device_sn left join car_info car on car.id = cd.car_id left join etp_info etp on etp.id = car.etp_id
car.del_flag = 0 and car.etp_id in
#{item}
and trip.device_sn =#{query.deviceSn}
and car.lic_code like concat('%',#{query.licCode},'%')
and trip.end_time>=#{query.startTime}
and trip.end_time <= concat(#{query.endTime},' 23:59:59')

SELECT trip.*, car.lic_code FROM device_trip trip LEFT JOIN car_device cardev ON trip.device_sn = cardev.device_sn INNER JOIN car_info car ON car.id = cardev.car_id
and car.lic_code like concat('%',#{query.licCode},'%')
and car.etp_id=#{query.etpId}
and trip.sta_time>=#{query.startTime}
and trip.end_time <=concat(#{query.endTime},' 23:59:59')
order by trip.create_time desc

SELECT ( SELECT lic_code FROM car_info car INNER JOIN car_device carDev ON car.id = carDev.car_id WHERE carDev.device_sn = trip.device_sn ) AS lic_code, sum( CASE WHEN DAY ( trip.create_time )= '1' THEN trip.mileage ELSE 0 END ) AS startMonthMileage, sum( CASE DAY ( trip.create_time ) WHEN '30' THEN trip.mileage WHEN '31' THEN trip.mileage ELSE 0 END ) AS endMonthMileage, sum( trip.mileage ) AS monthMileage, sum( CASE WHEN DAY ( trip.create_time )= '1' THEN trip.fuel_consumption ELSE 0 END ) AS startFuelConsumption, sum( CASE DAY ( trip.create_time ) WHEN '30' THEN trip.fuel_consumption WHEN '31' THEN trip.fuel_consumption ELSE 0 END ) AS endFuelConsumption, sum( trip.fuel_consumption ) AS monthFuelConsumption, sum( trip.fuel_consumption / 100 ) AS monthFuelOfKilometers FROM device_trip trip LEFT JOIN device dev ON trip.device_sn = dev.device_sn WHERE MONTH(trip.create_time)=#{query.month}
AND YEAR(trip.create_time)=#{query.year}
and dev.etp_id=#{query.etpId}
GROUP BY trip.device_sn
having lic_code like concat('%',#{query.licCode},'%')

SELECT ( SELECT lic_code FROM car_info car INNER JOIN car_device carDev ON car.id = carDev.car_id WHERE carDev.device_sn = trip.device_sn ) AS lic_code, sum( CASE WHEN DAY ( trip.create_time )= '1' THEN trip.mileage ELSE 0 END ) AS startMonthMileage, sum( CASE DAY ( trip.create_time ) WHEN '30' THEN trip.mileage WHEN '31' THEN trip.mileage ELSE 0 END ) AS endMonthMileage, sum( trip.mileage ) AS monthMileage, sum( CASE WHEN DAY ( trip.create_time )= '1' THEN trip.fuel_consumption ELSE 0 END ) AS startFuelConsumption, sum( CASE DAY ( trip.create_time ) WHEN '30' THEN trip.fuel_consumption WHEN '31' THEN trip.fuel_consumption ELSE 0 END ) AS endFuelConsumption, sum( trip.fuel_consumption ) AS monthFuelConsumption, sum( trip.fuel_consumption / 100 ) AS monthFuelOfKilometers FROM device_trip trip LEFT JOIN device dev ON trip.device_sn = dev.device_sn WHERE MONTH(trip.create_time)=#{query.month}
AND YEAR(trip.create_time)=#{query.year}
and dev.etp_id=#{query.etpId}
GROUP BY trip.device_sn
having lic_code like concat('%',#{query.licCode},'%')

SELECT car.lic_code, trip.*, round(TIMESTAMPDIFF( second, trip.sta_time, trip.end_time )/3600,1)AS timeLong -- , -- (select sum(round(TIMESTAMPDIFF( second, sta_time,end_time )/3600,1) ) from device_trip) -- as totaltimeLong FROM device_trip trip left join car_device cd on trip.device_sn = cd.device_sn left join car_info car on car.id = cd.car_id
car.del_flag = 0
and trip.device_sn =#{query.deviceSn}
and car.etp_id=#{query.etpId}
and car.lic_code like concat('%',#{query.licCode},'%')
and trip.sta_time >= #{query.startTime}
and trip.end_time <= concat(#{query.endTime},' 23:59:59')
order by trip.create_time desc
