mapper.select:
SELECT c.lic_code, u.nick_name as username, item.* FROM car_ins_item item LEFT JOIN car_info c ON item.car_id=c.`id` LEFT JOIN sys_user u ON u.user_id=item.`user_id` where item.`del_flag`=0 AND item.etp_id = #{query.etpId} and item.item_status!=0
and item.id IN (${query.ids})
AND item.start_time >=#{query.startTime}
AND item.start_time <=#{query.endTime}
AND c.lic_code like concat('%',#{query.licCode},'%')
AND item.item_status = #{query.itemStatus}
order by item.create_time desc

SELECT
c.lic_code,
u.username,
item.*
FROM
car_ins_item item
LEFT JOIN car_info c ON item.car_id = c.`id`
LEFT JOIN sys_user u ON u.user_id = item.`user_id`

    item.`del_flag`=0  and item.item_status !=0

        AND item.start_time >=#{query.startTime}


        AND item.start_time <=#{query.endTime}


        AND c.lic_code like concat('%',#{query.licCode},'%')


        AND item.item_status = #{query.itemStatus}


        AND item.etp_id = #{query.etpId}


        AND (item.user_id=#{query.userId}


        or c.lic_code in

            #{item}

        )


order by item.create_time desc
