mapper.select:
SELECT MONTH(trip.sta_time) AS md, SUM(trip.mileage / 1000) AS mileaGeTal, SUM(round(TIMESTAMPDIFF( second, trip.sta_time, trip.end_time )/3600,1))AS timeLong, SUM(fuel_consumption) AS consumPtionTal, (SUM(fuel_consumption) / SUM(trip.mileage / 1000) ) * 100 AS hdredKm FROM device_trip trip LEFT JOIN device dev ON trip.device_sn = dev.device_sn LEFT JOIN car_device car ON car.device_sn = trip.device_sn WHERE trip.`end_time` <= #{endTime} AND trip.`end_time` >= #{staTime} AND dev.etp_id = #{etpId}
AND car.car_id in ( ${dvgIdStar} )
GROUP BY MONTH(trip.`sta_time`)

SELECT info.lic_code AS vce, SUM(trip.mileage/1000) AS mileaGeTal, SUM(round(TIMESTAMPDIFF( second, trip.sta_time, trip.end_time )/3600,1))AS timeLong, SUM(fuel_consumption) AS consumPtionTal, (SUM(fuel_consumption) / SUM(trip.mileage / 1000) ) * 100 AS hdredKm, Max(trip.mileage/1000) AS omtNumber FROM device_trip trip LEFT JOIN device dev ON trip.device_sn = dev.device_sn LEFT JOIN car_device car ON car.device_sn = trip.device_sn LEFT JOIN car_info info ON info.id = car.car_id WHERE trip.`end_time` <= #{endTime} AND trip.`end_time` >= #{staTime} AND dev.etp_id = #{etpId}
AND info.dept_id in ( ${deptId} )
GROUP BY info.lic_code

SELECT users.nick_name AS vce, SUM(trip.mileage/1000) AS mileaGeTal, SUM(round(TIMESTAMPDIFF( second, trip.sta_time, trip.end_time )/3600,1))AS timeLong, SUM(fuel_consumption) AS consumPtionTal, (SUM(fuel_consumption) / SUM(trip.mileage / 1000) ) * 100 AS hdredKm, Max(trip.mileage/1000) AS omtNumber FROM device_trip trip LEFT JOIN device dev ON trip.device_sn = dev.device_sn LEFT JOIN car_device car ON car.device_sn = trip.device_sn LEFT JOIN car_info info ON info.id = car.car_id inner JOIN car_driver_info driverinfo ON driverinfo.id = info.driver_id inner JOIN sys_user users ON users.user_id = driverinfo.user_id WHERE trip.`end_time` <= #{endTime} AND trip.`end_time` >= #{staTime} AND dev.etp_id = #{etpId}
AND info.dept_id in ( ${deptId} )
GROUP BY users.nick_name
